import React from "react";
import "./Search.css";

const Search = (props) => {
  const [isActive, setIsActive] = useState(false);

  const handleChange = (e) => {
    if (value !== "") {
      setIsActive(true);
    } else {
      setIsActive(false);
    }
  };

  return (
    <form className="search-form" onSubmit={(e) => props.onSubmit(e)}>
      <div className="float-label">
        <input
          className="search"
          type="title"
          name="title"
          value={props.value}
          onChange={(e) => props.onChange(e)}
          onChange={handleChange}

          // autoFocus
        />
        <label className={isActive ? "Active" : ""} htmlFor="search">
          Search
        </label>
      </div>
      {/* <input
        className="search-input"
        value={props.value}
        onChange={(e) => props.onChange(e)}
        name="Search"
        placeholder="Search"
        type="text"
        autoFocus
      /> */}
    </form>
  );
};

export default Search;
