import React from "react";
import { Link } from "react-router-dom";
import "./Nav.css";
import Search from "./Search";

export default function Nav(props) {
  const { currentUser, handleLogout } = props;
  const { allPosts } = props;
  const [queriedPosts, setQueriedPosts] = useState([]);

  const handleSearch = (e) => {
    const newQueriedPost = allPosts.filter((post) =>
      post.destination.toLowerCase().includes(e.target.value.toLowerCase())
    );
    setQueriedPosts(newQueriedPost);
  };
  console.log(queriedPosts.length);
  console.log(allPosts);

  const handleSubmit = (event) => event.preventDefault();

  const allPostJSX = allPosts.map((post, index) => <PostPreview post={post} />);
  console.log(allPostJSX);

  const postJSX = queriedPosts.map((post, index) => (
    <PostPreview post={post} />
  ));
  return (
    <header>
      <div className="nav">
        <Link to="/">
          <a className="left-side">
            <img src="https://i.imgur.com/JSkNgG8.png?1" />
            <h1 className="logo">Travel Like A Local</h1>
          </a>
        </Link>
        <Search onSubmit={handleSubmit} onChange={handleSearch} />
        {currentUser ? (
          <div className="right-side">
            <Link to="/myaccount">
              <p>{currentUser.username}'s Account</p>
            </Link>
            <button className="button" onClick={handleLogout}>
              Logout
            </button>
          </div>
        ) : (
          <div className="right-side">
            <button className="button">
              <Link className="button-text" to="/signin">
                SIGNIN
              </Link>
            </button>
            <button className="button">
              <Link className="button-text" to="/signup">
                SIGNUP
              </Link>
            </button>
          </div>
        )}
      </div>

      {props.children}
    </header>
  );
}
